# ==================== SYSTEM ANALYSIS ====================

class SystemAnalyzer:
	"""Native system info + pre-installed tool detection — no backend required."""

	@staticmethod
	def collect():
		"""Collect full system info using pure Python / /proc. Returns a dict."""
		info = {}
		sys_os = _platform.system().lower()

		# ── OS / Distro ───────────────────────────────────────────────
		if sys_os == 'linux':
			try:
				od = {}
				with open('/etc/os-release') as f:
					for line in f:
						if '=' in line:
							k, v = line.strip().split('=', 1)
							od[k] = v.strip('"')
				info['os']        = od.get('PRETTY_NAME', 'Linux')
				info['distro_id'] = od.get('ID', 'linux').lower()
			except Exception:
				info['os'] = 'Linux'; info['distro_id'] = 'linux'
		elif sys_os == 'darwin':
			ver = _platform.mac_ver()[0]
			info['os'] = f'macOS {ver}' if ver else 'macOS'
			info['distro_id'] = 'macos'
		else:
			info['os'] = _platform.system(); info['distro_id'] = sys_os

		# ── Kernel / Architecture ─────────────────────────────────────
		info['kernel'] = _platform.release()
		info['arch']   = _platform.machine()

		# ── Package manager ───────────────────────────────────────────
		for pm in ('apt', 'dnf', 'pacman', 'zypper', 'brew', 'apk'):
			if _shutil.which(pm):
				info['pkg_mgr'] = pm; break
		else:
			info['pkg_mgr'] = 'unknown'

		# ── CPU ───────────────────────────────────────────────────────
		try:
			import multiprocessing
			info['cpu_cores'] = multiprocessing.cpu_count()
		except Exception:
			info['cpu_cores'] = 'N/A'

		# CPU model from /proc/cpuinfo when available
		try:
			with open('/proc/cpuinfo') as f:
				for line in f:
					if line.startswith('model name'):
						info['cpu_model'] = line.split(':', 1)[1].strip()
						break
		except Exception:
			pass

		# ── RAM (/proc/meminfo) ───────────────────────────────────────
		try:
			mem = {}
			with open('/proc/meminfo') as f:
				for line in f:
					m = re.match(r'(\w+):\s+(\d+)', line)
					if m:
						mem[m.group(1)] = int(m.group(2))
			info['total_ram_mb'] = mem.get('MemTotal', 0) // 1024
			info['avail_ram_mb'] = mem.get('MemAvailable', 0) // 1024
			# Legacy keys for print_system_info compatibility
			info['total_ram'] = info['total_ram_mb']
			info['avail_ram'] = info['avail_ram_mb']
		except Exception:
			info['total_ram_mb'] = info['avail_ram_mb'] = 0
			info['total_ram'] = info['avail_ram'] = 0

		# ── Disk (/) ──────────────────────────────────────────────────
		try:
			st = os.statvfs('/')
			info['total_disk_gb'] = round((st.f_blocks * st.f_frsize) / 1024**3, 2)
			info['avail_disk_gb'] = round((st.f_bavail * st.f_frsize) / 1024**3, 2)
			# Legacy keys
			info['total_disk'] = info['total_disk_gb']
			info['avail_disk'] = info['avail_disk_gb']
		except Exception:
			info['total_disk_gb'] = info['avail_disk_gb'] = 0
			info['total_disk'] = info['avail_disk'] = 0

		return info

	@staticmethod
	def detect_tools(db):
		"""
		Check every tool in the DB against the system PATH.
		Returns dict: { tool_key: { binary, path, category, version } }
		"""
		detected = {}
		for cat, tools in db.items():
			if cat == 'profiles':
				continue
			for tname, tdata in tools.items():
				binary = tdata.get('binary', tname)
				path = _shutil.which(binary)
				if not path:
					# Also try the tool name itself as binary fallback
					path = _shutil.which(tname)
				if path:
					entry = {'binary': binary, 'path': path, 'category': cat}
					# Try to get version string cheaply
					for ver_flag in ('--version', '-V', '-v', 'version'):
						try:
							r = subprocess.run(
								[binary, ver_flag],
								capture_output=True, text=True, timeout=3
							)
							out = (r.stdout or r.stderr or '').strip().splitlines()
							if out:
								entry['version'] = out[0][:80]
								break
						except Exception:
							continue
					detected[tname] = entry
		return detected

	@staticmethod
	def compute_compat(tdata, sysinfo):
		"""
		Compatibility score 0–100 for a single tool entry.
		  40 pts — package manager supported
		  30 pts — RAM available vs required
		  30 pts — architecture
		"""
		score = 0
		pkg_mgr  = sysinfo.get('pkg_mgr', 'unknown')
		packages = tdata.get('packages', {})
		avail    = sysinfo.get('avail_ram_mb', 9999)
		req      = tdata.get('metrics', {}).get('ram_required_mb', 0)
		arch     = sysinfo.get('arch', 'x86_64').lower()

		# Package manager support
		if pkg_mgr in packages:
			score += 40
		elif packages:
			score += 20   # has support for something, just not this PM

		# RAM
		if req == 0 or avail >= req * 2:
			score += 30
		elif avail >= req:
			score += 20
		elif avail >= req // 2:
			score += 10

		# Architecture
		if arch in ('x86_64', 'amd64'):
			score += 30
		elif 'aarch64' in arch or 'arm64' in arch:
			score += 25
		elif 'arm' in arch:
			score += 15
		else:
			score += 20

		return min(score, 100)

	@classmethod
	def build_warnings(cls, sysinfo, detected, db):
		"""
		Return a list of (level, message) warning tuples based on system state.
		level: 'critical' | 'warning' | 'info'
		"""
		warnings = []
		avail_ram = sysinfo.get('avail_ram_mb', 9999)
		total_ram = sysinfo.get('total_ram_mb', 9999)
		avail_disk = sysinfo.get('avail_disk_gb', 999)
		pkg_mgr = sysinfo.get('pkg_mgr', 'unknown')
		arch = sysinfo.get('arch', 'x86_64').lower()

		# RAM warnings
		if avail_ram < 256:
			warnings.append(('critical', f'Critically low available RAM ({avail_ram} MB). Most tools will fail to run.'))
		elif avail_ram < 512:
			warnings.append(('critical', f'Very low available RAM ({avail_ram} MB). ClamAV, AIDE, and heavy tools will fail.'))
		elif avail_ram < 1024:
			warnings.append(('warning', f'Low available RAM ({avail_ram} MB). Heavy tools (ClamAV, OpenVAS) may be slow.'))
		if total_ram < 2048:
			warnings.append(('warning', f'Total RAM is {total_ram} MB. Consider adding RAM for optimal security tool performance.'))

		# Disk warnings
		if avail_disk < 2:
			warnings.append(('critical', f'Critically low disk space ({avail_disk} GB free). Tool installation will likely fail.'))
		elif avail_disk < 5:
			warnings.append(('warning', f'Low disk space ({avail_disk} GB free). Large tools (ClamAV, OSQuery) may fail to install.'))

		# Package manager
		if pkg_mgr == 'unknown':
			warnings.append(('critical', 'No supported package manager found (apt/dnf/pacman/zypper). Cannot install tools automatically.'))

		# Architecture
		if 'arm' in arch and 'aarch64' not in arch and 'arm64' not in arch:
			warnings.append(('warning', f'Architecture {sysinfo.get("arch")} has limited tool support. Some tools may not be available.'))

		# High-RAM tools installed but RAM is tight
		heavy_tools = {'clamav': 512, 'osquery': 256, 'aide': 256}
		for tname, ram_needed in heavy_tools.items():
			if tname in detected and avail_ram < ram_needed:
				warnings.append(('warning', f'{tname} is installed but available RAM ({avail_ram} MB) is below its {ram_needed} MB requirement.'))

		# Pre-installed tools detected — info
		pre = len(detected)
		if pre > 0:
			tool_list = ', '.join(list(detected.keys())[:5])
			suffix = f' (+{pre-5} more)' if pre > 5 else ''
			warnings.append(('info', f'{pre} security tool(s) already installed on this system: {tool_list}{suffix}'))

		return warnings

	@classmethod
	def save(cls, data):
		"""Persist analysis to ~/.kjer/system_analysis.json"""
		import datetime
		try:
			ANALYSIS_FILE.parent.mkdir(parents=True, exist_ok=True)
			data['analyzed_at'] = datetime.datetime.now().isoformat()
			ANALYSIS_FILE.write_text(json.dumps(data, indent=2))
		except Exception:
			pass

	@classmethod
	def load(cls):
		"""Load cached analysis. Returns dict or None."""
		try:
			if ANALYSIS_FILE.exists():
				return json.loads(ANALYSIS_FILE.read_text())
		except Exception:
			pass
		return None



# ==================== BACKEND API INTEGRATION ====================

class BackendAPI:
	"""Integration with backend_api.py for tool ops and license_manager.py for licensing."""

	@staticmethod
	def call_license_manager(action, **kwargs):
		"""Execute license_manager.py for all license-related operations."""
		args = [sys.executable, str(LICENSE_MANAGER), action]
		if 'license_key' in kwargs:
			args.extend(['--license-key', kwargs['license_key']])
		try:
			result = subprocess.run(args, capture_output=True, text=True, timeout=30)
			if result.stdout:
				try:
					return json.loads(result.stdout)
				except json.JSONDecodeError:
					return {'success': False, 'error': result.stdout.strip()}
			return {'success': result.returncode == 0, 'error': result.stderr.strip()}
		except subprocess.TimeoutExpired:
			return {'success': False, 'error': 'License check timed out'}
		except Exception as e:
			return {'success': False, 'error': str(e)}

	@staticmethod
	def call_backend(action, **kwargs):
		"""Execute backend_api.py for tool/system operations."""
		args = [sys.executable, str(BACKEND_API), action]
		
		# Add optional parameters
		if 'tool' in kwargs:
			args.extend(['--tool', kwargs['tool']])
		if 'profile' in kwargs:
			args.extend(['--profile', kwargs['profile']])
		if 'license_key' in kwargs:
			args.extend(['--license-key', kwargs['license_key']])
		if 'license_type' in kwargs:
			args.extend(['--license-type', kwargs['license_type']])
		
		try:
			result = subprocess.run(args, capture_output=True, text=True, timeout=300)
			if result.stdout:
				try:
					return json.loads(result.stdout)
				except json.JSONDecodeError:
					return {'success': True, 'message': result.stdout.strip()}
			return {'success': result.returncode == 0, 'stderr': result.stderr}
		except subprocess.TimeoutExpired:
			return {'success': False, 'error': 'Operation timed out'}
		except Exception as e:
			return {'success': False, 'error': str(e)}
	
	@classmethod
	def check_activation(cls):
		"""Check if Kjer is activated — routed through license_manager."""
		return cls.call_license_manager('check-activation')
	
	@classmethod
	def install_tool(cls, tool_name):
		"""Install a specific tool"""
		return cls.call_backend('install', tool=tool_name)
	
	@classmethod
	def uninstall_tool(cls, tool_name):
		"""Uninstall a specific tool"""
		return cls.call_backend('uninstall', tool=tool_name)
	
	@classmethod
	def get_installed_tools(cls):
		"""Get list of installed tools"""
		return cls.call_backend('list-installed')
	
	@classmethod
	def install_profile(cls, profile_name):
		"""Install a tool profile"""
		return cls.call_backend('install-profile', profile=profile_name)
	
	@classmethod
	def get_system_status(cls):
		"""Get system and tool status"""
		return cls.call_backend('system-status')

	@classmethod
	def uninitialize(cls):
		"""Uninitialize Kjer - clear all initialization and activation state"""
		return cls.call_backend('uninitialize')

	@classmethod
	def activate_license(cls, license_key, license_type='personal'):
		"""Activate a license key — routed through license_manager."""
		return cls.call_license_manager('activate', license_key=license_key)

	@classmethod
	def get_license_info(cls):
		"""Get detailed license status — routed through license_manager."""
		return cls.call_license_manager('license-info')

	@classmethod
	def reinitialize(cls):
		"""Trigger a full reinitialization of the Kjer system"""
		return cls.call_backend('reinitialize')

# ==================== UTILITY FUNCTIONS ====================

def load_db():
	with open(DB_PATH, 'r') as f:
		return yaml.safe_load(f)

def check_initialization():
	"""Check if Kjer has been initialized via GUI.
	Initialization does not require a key — only upgrades do.
	Primary check: ~/.kjer/initialized flag written by initializeKjer() in the GUI.
	Fallback: ~/.kjer/install_state.json written by the installer."""
	if INIT_FLAG.exists():
		return True, {'initialized': True}
	if INSTALL_STATE.exists():
		return True, {'initialized': True, 'source': 'install_state'}
	return False, {'error': 'Not initialized. Launch the Kjer GUI and click Initialize.'}

def check_os_compatibility():
	"""Check if the correct CLI version is being used for the detected OS.
	Reads from install_state.json (written at install time) as the primary source."""
	# Primary: install_state.json — stored at install time by kjer-install.sh
	try:
		if INSTALL_STATE.exists():
			state = json.loads(INSTALL_STATE.read_text())
			detected_os = state.get('os', '').lower()
			if detected_os:
				expected_os = 'linux'
				if detected_os != expected_os:
					distro = state.get('distro', detected_os.upper())
					return False, detected_os, f'This is the Linux CLI version. Your system was detected as {distro} during installation.'
				return True, detected_os, 'OS compatibility verified'
	except Exception:
		pass

	# Fallback: read from backend (set during GUI initialization)
	backend = BackendAPI()
	system_status = backend.get_system_status()
	
	if not system_status.get('success'):
		return False, 'unknown', 'Unable to retrieve system information from backend'
	
	# Try to get detected_os from top level first, then fallback to system_info
	detected_os = system_status.get('detected_os', '').lower()
	
	if not detected_os:
		# Fallback: check system_info for OS information
		system_info = system_status.get('system_info', {})
		detected_os = system_info.get('os', '').lower()
		
		# The backend might return full OS names like "Ubuntu 25.10"
		# We need to determine if it's Linux, Windows, or macOS
		if 'ubuntu' in detected_os or 'debian' in detected_os or 'fedora' in detected_os or 'arch' in detected_os or 'linux' in detected_os:
			detected_os = 'linux'
		elif 'windows' in detected_os or 'win' in detected_os:
			detected_os = 'windows'
		elif 'mac' in detected_os or 'darwin' in detected_os:
			detected_os = 'macos'
	
	if not detected_os or detected_os == 'n/a':
		return False, 'unknown', 'OS not detected. Run the installer (installer/kjer-install.sh) to set up the system.'
	
	# This is the Linux CLI version - it should only run on Linux systems
	expected_os = 'linux'
	
	if detected_os != expected_os:
		return False, detected_os, f'This is the Linux CLI version. Your system was detected as {detected_os.upper()} during initialization.'
	
	return True, detected_os, 'OS compatibility verified'

def get_system_info():
	"""
	Get system information — native Python first, backend as fallback.
	Always returns all expected keys so callers don't need to guard.
	"""
	# Always try native collection first (fast, no backend dep)
	info = SystemAnalyzer.collect()
	if info.get('os') and info.get('os') != 'N/A':
		return info

	# Fallback: backend (post-initialization only)
	try:
		backend = BackendAPI()
		system_status = backend.get_system_status()
		if system_status.get('success'):
			bi = system_status.get('system_info', {})
			if bi:
				return bi
	except Exception:
		pass

	# Last resort: empty structure
	return {
		'os': 'N/A', 'distro_id': 'N/A', 'kernel': 'N/A', 'arch': 'N/A',
		'pkg_mgr': 'N/A', 'cpu_cores': 'N/A',
		'total_ram': 'N/A', 'avail_ram': 'N/A',
		'total_ram_mb': 0,  'avail_ram_mb': 0,
		'total_disk': 'N/A', 'avail_disk': 'N/A',
		'total_disk_gb': 0,  'avail_disk_gb': 0,
	}

def _get_detected_os_label():
	"""Get the OS label from install_state.json (written at install time).
	Falls back to the backend-initialized state if the file is not present."""
	# Primary: install_state.json written by kjer-install.sh
	try:
		if INSTALL_STATE.exists():
			state = json.loads(INSTALL_STATE.read_text())
			distro = state.get('distro', '')
			os_key = state.get('os', '').lower()
			if os_key:
				# Return distro name for display (e.g. "Ubuntu 24.04 LTS")
				return distro if distro else os_key
	except Exception:
		pass

	# Fallback: backend system-status (set during GUI initialization)
	try:
		status = BackendAPI.get_system_status()
		detected = (status.get('detected_os') or '').lower()
		if not detected:
			# parse system_info.os string
			os_raw = (status.get('system_info', {}).get('os') or '').lower()
			if any(d in os_raw for d in ('ubuntu', 'debian', 'fedora', 'arch', 'linux')):
				detected = 'linux'
			elif any(d in os_raw for d in ('mac', 'darwin')):
				detected = 'macos'
			elif any(d in os_raw for d in ('windows', 'win')):
				detected = 'windows'
		return detected if detected else None
	except Exception:
		return None


def print_banner():
	"""Print ASCII art banner"""
	# OS label only comes from the backend-detected OS (set during GUI initialization)
	os_label = _get_detected_os_label()
	version_line = f'v1.0.0 ({os_label})' if os_label else 'v1.0.0'
	print("\n\033[1;35m")  # Bold magenta
	print("██╗  ██╗     ██╗███████╗██████╗ ")
	print("██║ ██╔╝     ██║██╔════╝██╔══██╗")
	print("█████╔╝      ██║█████╗  ██████╔╝")
	print("██╔═██╗ ██   ██║██╔══╝  ██╔══██╗")
	print("██║  ██╗╚█████╔╝███████╗██║  ██║")
	print("╚═╝  ╚═╝ ╚════╝ ╚══════╝╚═╝  ╚═╝")
	print(f"\033[1;36m         {version_line:^30}    \033[0m")
	print("\033[0;37m    Professional Cybersecurity Tool Management Platform    \033[0m")
	print()

def print_system_info():
	"""Print system information section — native data with inline warnings."""
	info = get_system_info()

	# ── Inline warning glyphs ──────────────────────────────────────────
	ram_mb   = info.get('avail_ram_mb', 0)
	disk_gb  = info.get('avail_disk_gb', 0)
	ram_warn  = ''
	disk_warn = ''
	if   ram_mb < 256:  ram_warn  = '  \033[1;31m[CRITICAL — extremely low]\033[0m'
	elif ram_mb < 512:  ram_warn  = '  \033[1;31m[CRITICAL — very low]\033[0m'
	elif ram_mb < 1024: ram_warn  = '  \033[1;33m[WARNING — low]\033[0m'
	elif ram_mb < 2048: ram_warn  = '  \033[1;33m[WARNING]\033[0m'
	if   disk_gb < 2:   disk_warn = '  \033[1;31m[CRITICAL — very low]\033[0m'
	elif disk_gb < 5:   disk_warn = '  \033[1;33m[WARNING — low]\033[0m'

	print("\033[1;33m" + "="*70)
	print("  SYSTEM INFORMATION")
	print("="*70 + "\033[0m")
	print(f"  \033[1;32mOS:\033[0m              {info.get('os','N/A')}")
	print(f"  \033[1;32mDistro ID:\033[0m       {info.get('distro_id','N/A')}")
	print(f"  \033[1;32mKernel:\033[0m          {info.get('kernel','N/A')}")
	print(f"  \033[1;32mArchitecture:\033[0m    {info.get('arch','N/A')}")
	print(f"  \033[1;32mPackage Mgr:\033[0m     {info.get('pkg_mgr','N/A')}")
	print(f"  \033[1;32mCPU Cores:\033[0m       {info.get('cpu_cores','N/A')}")
	print(f"  \033[1;32mTotal RAM:\033[0m       {info.get('total_ram','N/A')} MB  "
	      f"(available: {info.get('avail_ram','N/A')} MB){ram_warn}")
	print(f"  \033[1;32mTotal Disk:\033[0m      {info.get('total_disk','N/A')} GB  "
	      f"(available: {info.get('avail_disk','N/A')} GB){disk_warn}")
	print("\033[1;33m" + "="*70 + "\033[0m")
	print()

def print_header():
	"""Print full header with banner and system info"""
	os.system('clear' if os.name != 'nt' else 'cls')
	print_banner()
	print_system_info()

def list_tools(db):
	"""Display all available tools — HakPak3-style compatibility table."""
	info     = get_system_info()
	detected = SystemAnalyzer.detect_tools(db)

	# ── gather rows ───────────────────────────────────────────────────
	rows = []
	for cat, tools in db.items():
		if cat == 'profiles':
			continue
		for tname, tdata in tools.items():
			compat   = SystemAnalyzer.compute_compat(tdata, info)
			size_mb  = tdata.get('metrics', {}).get('estimated_size_mb', 0)
			ram_mb   = tdata.get('metrics', {}).get('ram_required_mb', 0)
			det      = detected.get(tname, {})
			if det.get('installed'):
				ver   = det.get('version', '')
				vstxt = f'  ({ver})' if ver else ''
				status_str = f'\033[1;32m✓ Installed{vstxt}\033[0m'
			else:
				status_str = '  Available'
			rows.append((tname, compat, size_mb, ram_mb, status_str, tdata.get('description', ''), cat))

	# ── sort: installed first, then by compat desc ────────────────────
	rows.sort(key=lambda r: (0 if '✓' in r[4] else 1, -r[1]))

	total       = len(rows)
	n_installed = sum(1 for r in rows if '✓' in r[4])

	W = 94  # total table width
	print()
	print("\033[1;33m" + "="*W)
	print(f"  ALL TOOLS ({total})  —  ✓ = already installed on this system  |  detected: {n_installed}/{total}")
	print("="*W + "\033[0m")
	print(f"  {'Tool':<22} {'Compat':>6}  {'Size':>9}  {'RAM':>9}  {'Category':<12} {'Status':<24} Description")
	print("\033[0;33m" + "-"*W + "\033[0m")

	for tname, compat, size_mb, ram_mb, status_str, desc, cat in rows:
		# Compat colour
		if   compat >= 80: ccolor = '\033[1;32m'
		elif compat >= 50: ccolor = '\033[1;33m'
		else:              ccolor = '\033[1;31m'
		compat_col = f'{ccolor}{compat:>3}%\033[0m'
		size_col   = f'{size_mb:>7.1f} MB' if size_mb else '       N/A'
		ram_col    = f'{ram_mb:>7} MB'     if ram_mb  else '       N/A'
		# Truncate description
		max_desc   = W - 22 - 6 - 9 - 9 - 12 - 20 - 16
		desc_col   = desc[:max_desc] + '…' if len(desc) > max_desc else desc
		# Strip ANSI to measure status string length for padding
		plain_status = re.sub(r'\033\[[0-9;]*m', '', status_str)
		pad          = max(0, 24 - len(plain_status))
		print(f"  \033[1;36m{tname:<22}\033[0m {compat_col:>15}  {size_col}  {ram_col}  {cat:<12} {status_str}{' '*pad} {desc_col}")

	print("\033[1;33m" + "="*W + "\033[0m")
	print()


def run_system_analysis(db):
	"""Full system analysis — system info, warnings, tool compat table, save JSON."""
	print()
	print("\033[1;35m" + "╔" + "═"*68 + "╗")
	print("║" + "  KJER SYSTEM ANALYSIS & COMPATIBILITY REPORT".center(68) + "║")
	print("╚" + "═"*68 + "╝" + "\033[0m")
	print()

	# ── 1. Collect system info ─────────────────────────────────────────
	print("\033[1;36m→ Collecting system information…\033[0m")
	info = SystemAnalyzer.collect()

	# ── 2. Detect pre-installed tools ─────────────────────────────────
	print("\033[1;36m→ Scanning for pre-installed tools…\033[0m")
	detected = SystemAnalyzer.detect_tools(db)

	# ── 3. Build warnings ─────────────────────────────────────────────
	warnings = SystemAnalyzer.build_warnings(info, detected, db)

	# ── 4. Save results ───────────────────────────────────────────────
	analysis = {
		**info,
		'detected_tools': detected,
		'warnings':       [{'level': w[0], 'message': w[1]} for w in warnings],
	}
	SystemAnalyzer.save(analysis)

	# ── Print system info block ────────────────────────────────────────
	W = 70
	print()
	print("\033[1;33m" + "="*W)
	print("  SYSTEM INFORMATION")
	print("="*W + "\033[0m")
	fields = [
		('OS',           info.get('os','N/A')),
		('Distro ID',    info.get('distro_id','N/A')),
		('Kernel',       info.get('kernel','N/A')),
		('Architecture', info.get('arch','N/A')),
		('Package Mgr',  info.get('pkg_mgr','N/A')),
		('CPU Cores',    info.get('cpu_cores','N/A')),
		('Total RAM',    f"{info.get('total_ram','N/A')} MB  (available: {info.get('avail_ram','N/A')} MB)"),
		('Total Disk',   f"{info.get('total_disk','N/A')} GB  (available: {info.get('avail_disk','N/A')} GB)"),
	]
	for label, val in fields:
		print(f"  \033[1;32m{label:<14}\033[0m  {val}")

	# ── Print warnings block ───────────────────────────────────────────
	if warnings:
		print()
		print("\033[1;33m" + "="*W)
		print("  WARNINGS & NOTICES")
		print("="*W + "\033[0m")
		level_order = {'critical': 0, 'warning': 1, 'info': 2}
		for lvl, msg in sorted(warnings, key=lambda w: level_order.get(w[0], 9)):
			if   lvl == 'critical': glyph = '\033[1;31m [CRITICAL]\033[0m'
			elif lvl == 'warning':  glyph = '\033[1;33m [WARNING] \033[0m'
			else:                   glyph = '\033[0;36m [INFO]    \033[0m'
			print(f"  {glyph}  {msg}")

	# ── Print tool compat table ────────────────────────────────────────
	print()
	list_tools(db)

	# ── Print pre-installed summary ────────────────────────────────────
	pre = [(tn, d) for tn, d in detected.items() if d.get('installed')]
	if pre:
		print("\033[1;33m" + "="*W)
		print(f"  PRE-INSTALLED TOOLS DETECTED ON THIS SYSTEM ({len(pre)})")
		print("="*W + "\033[0m")
		for tn, d in sorted(pre):
			ver   = d.get('version', '')
			path  = d.get('path', '')
			vstxt = f'  v{ver}' if ver else ''
			print(f"  \033[1;32m✓\033[0m  {tn:<20}  {path}{vstxt}")
		print()

	print(f"\033[0;33m→ Analysis saved to {ANALYSIS_FILE}\033[0m")
	print()



def list_profiles(db):
	"""Display installation profiles"""
	print("\n\033[1;36m" + "="*70)
	print("  INSTALLATION PROFILES")
	print("="*70 + "\033[0m")
	
	if not db or 'profiles' not in db:
		print("\n\033[1;31m✗ No profiles available\033[0m")
		return
	
	for pname, pdata in db['profiles'].items():
		print(f"\n\033[1;35m{pdata['name']}\033[0m")
		print(f"  {pdata['description']}")
		tool_count = len(pdata['tools'])
		tools_display = ', '.join(pdata['tools'][:5])
		if tool_count > 5:
			tools_display += f", ... (+{tool_count-5} more)"
		print(f"  \033[0;33mTools ({tool_count}):\033[0m {tools_display}")
	
	# Offer to install profile
	print("\n\033[1;33mInstall a profile? Enter profile name or press Enter to return:\033[0m")
	choice = input('\033[1;35m>\033[0m ').strip()
	
	if choice:
		# Find profile by name (case-insensitive)
		profile_key = None
		for pname, pdata in db['profiles'].items():
			if pdata['name'].lower() == choice.lower():
				profile_key = pname
				break
		
		if profile_key:
			install_profile_from_menu(profile_key, db)
		else:
			print(f"\033[1;31m✗ Profile '{choice}' not found\033[0m")

def install_profile_from_menu(profile_name, db):
	"""Install a profile from the menu"""
	if profile_name not in db['profiles']:
		print(f"\033[1;31m✗ Profile not found: {profile_name}\033[0m")
		return
	
	profile = db['profiles'][profile_name]
	print(f"\n\033[1;36m→ Installing profile: {profile['name']}\033[0m")
	print(f"   {profile['description']}")
	print(f"   \033[0;33mTools to install ({len(profile['tools'])}):\033[0m {', '.join(profile['tools'])}")
	
	confirm = input("\n\033[1;33mProceed with installation? (y/yes/no):\033[0m ").strip().lower()
	
	if confirm not in ('y', 'yes'):
		print("\033[0;33m✗ Profile installation cancelled\033[0m")
		return
	
	# Call backend to install profile
	backend = BackendAPI()
	result = backend.install_profile(profile_name)
	
	if result.get('success'):
		print(f"\n\033[1;32m✓ Profile '{profile['name']}' installed successfully!\033[0m")
		if result.get('tools_installed'):
			print(f"   \033[1;32mInstalled tools:\033[0m {', '.join(result['tools_installed'])}")
		if result.get('tools_failed'):
			print(f"   \033[1;31mFailed tools:\033[0m {', '.join(result['tools_failed'])}")
	else:
		print(f"\n\033[1;31m✗ Profile installation failed\033[0m")
		if result.get('error'):
			print(f"   Error: {result['error']}")

def install_tool(db):
	"""Install a specific tool"""
	all_tools = {t:cat for cat in db if cat != 'profiles' for t in db[cat]}
	print("\n\033[1;36mAvailable tools to install:\033[0m")
	for tname in sorted(all_tools):
		cat = all_tools[tname]
		desc = db[cat][tname].get('description', '')
		print(f"  \033[1;32m•\033[0m {tname:<20} - {desc}")
	
	print("\n\033[1;33mEnter tool name to install (or 'back' to return):\033[0m")
	tname = input('\033[1;35m>\033[0m ').strip()
	
	if tname.lower() == 'back':
		return
	
	if tname not in all_tools:
		print("\033[1;31m✗ Error: Tool not found.\033[0m")
		return
	
	cat = all_tools[tname]
	tdata = db[cat][tname]
	
	print(f"\n\033[1;36m→ Installing {tname}...\033[0m")
	print(f"   Category: {cat.capitalize()}")
	print(f"   Packages: {', '.join(tdata.get('packages', []))}")
	
	# Call backend API for actual installation
	result = BackendAPI.install_tool(tname)
	
	if result.get('success'):
		print(f"\n\033[1;32m✓ Successfully installed {tname}\033[0m")
		if result.get('message'):
			print(f"   {result['message']}")
	else:
		print(f"\n\033[1;31m✗ Failed to install {tname}\033[0m")
		if result.get('error'):
			print(f"   Error: {result['error']}")
		if result.get('stderr'):
			print(f"   {result['stderr']}")

