	result = BackendAPI.get_installed_tools()
	
	if not result.get('success') or not result.get('tools'):
		print("\033[0;33m  No tools currently installed.\033[0m")
		return
	
	installed = result['tools']
	print("\n\033[1;32mInstalled tools:\033[0m")
	for tool in installed:
		print(f"  \033[1;32m•\033[0m {tool}")
	
	print("\n\033[1;33mEnter tool name to uninstall (or 'back' to return):\033[0m")
	tname = input('\033[1;35m>\033[0m ').strip()
	
