def _ensure_electron(desktop_dir):
	"""
	Ensure Electron is available for launching the GUI.
	Priority:
	  1. desktop/node_modules/.bin/electron  (local install — preferred)
	  2. Global 'electron' on PATH
	  3. Auto-install: run 'npm install' in desktop/ using the OS-appropriate
	     package manager to first get Node.js if it's absent.
	Returns the path to the electron binary, or None on failure.
	"""
	import shutil, platform as _platform

	# 1. Local electron (most reliable — installed by npm install in desktop/)
	local_electron = desktop_dir / 'node_modules' / '.bin' / 'electron'
	if local_electron.exists():
		return str(local_electron)

	# 2. Global electron on PATH
	global_electron = shutil.which('electron')
	if global_electron:
		return global_electron

	# 3. Try to install via npm
	print("\033[1;33m→ Electron not found. Installing automatically...\033[0m")

	npm = shutil.which('npm')
	if not npm:
		# Node/npm not installed — use OS-appropriate package manager
		sys_os = _platform.system().lower()
		print("\033[1;36m→ Node.js not found — installing via system package manager...\033[0m")
		try:
			if sys_os == 'linux':
				# Detect package manager
				pm = None
				for candidate, cmd in [
					('apt-get',  ['sudo', 'apt-get', 'install', '-y', 'nodejs', 'npm']),
					('dnf',      ['sudo', 'dnf', 'install', '-y', 'nodejs', 'npm']),
					('pacman',   ['sudo', 'pacman', '-S', '--noconfirm', 'nodejs', 'npm']),
					('zypper',   ['sudo', 'zypper', 'install', '-y', 'nodejs', 'npm']),
				]:
					if shutil.which(candidate):
						pm = cmd
						break
				if pm:
					print(f"\033[1;36m→ Running: {' '.join(pm)}\033[0m")
					result = subprocess.run(pm, capture_output=False)
					if result.returncode != 0:
						print("\033[1;31m✗ Package manager install failed.\033[0m")
						return None
				else:
					print("\033[1;31m✗ No supported package manager found (apt/dnf/pacman/zypper).\033[0m")
					print("  Install Node.js manually: https://nodejs.org")
					return None
			elif sys_os == 'darwin':
				brew = shutil.which('brew')
				if brew:
					print("\033[1;36m→ Running: brew install node\033[0m")
					subprocess.run([brew, 'install', 'node'], check=False)
				else:
					print("\033[1;31m✗ Homebrew not found.\033[0m")
					print("  Install Node.js: https://nodejs.org  or  brew install node")
					return None
			else:
				print("\033[1;31m✗ Auto-install not supported on this OS.\033[0m")
				print("  Install Node.js manually: https://nodejs.org")
				return None
		except Exception as e:
			print(f"\033[1;31m✗ Node.js install failed: {e}\033[0m")
			return None

		npm = shutil.which('npm')
		if not npm:
			print("\033[1;31m✗ npm still not available after install. Open a new terminal and retry.\033[0m")
			return None

	# Run npm install in desktop/ to get local electron
	print(f"\033[1;36m→ Running: npm install (in {desktop_dir})\033[0m")
	try:
		result = subprocess.run([npm, 'install'], cwd=str(desktop_dir), capture_output=False)
		if result.returncode == 0 and local_electron.exists():
			print("\033[1;32m✓ Electron installed successfully.\033[0m")
			return str(local_electron)
		else:
			print("\033[1;31m✗ npm install completed but electron binary not found.\033[0m")
			return None
	except Exception as e:
		print(f"\033[1;31m✗ npm install failed: {e}\033[0m")
		return None


def run_kde_install():
	"""Run the kjer-install dependency bootstrapper — works before initialization."""
	import shutil, platform as _platform
	installer_dir = Path(__file__).parent.parent / 'installer'
	sys_os = _platform.system().lower()

	if sys_os in ('linux', 'darwin'):
		script = installer_dir / 'kjer-install.sh'
		if not script.exists():
			print(f"\033[1;31m✗ Installer not found: {script}\033[0m")
			sys.exit(1)
		os.chmod(script, 0o755)
		print("\033[1;32m→ Running Kjer dependency bootstrapper...\033[0m\n")
		result = subprocess.run(['bash', str(script)], check=False)
		os._exit(result.returncode)
	else:
		# Windows — invoke the PowerShell script
		script = installer_dir / 'kjer-install.ps1'
		if not script.exists():
			print(f"\033[1;31m✗ Installer not found: {script}\033[0m")
			sys.exit(1)
		print("\033[1;32m→ Running Kjer dependency bootstrapper...\033[0m\n")
		result = subprocess.run(
			['powershell', '-ExecutionPolicy', 'Bypass', '-File', str(script)],
			check=False
		)
		os._exit(result.returncode)


def launch_gui():
	"""Launch the Kjer GUI (Electron app)"""
	import shutil
	# Use resolve() so symlink-invoked paths point to the real Kjer directory
	desktop_dir = Path(__file__).resolve().parent.parent / 'desktop'

	# macOS .app bundle (distributed install)
	app_path = Path('/Applications/Kjer.app')
	if sys.platform == 'darwin' and app_path.exists():
		subprocess.Popen(['open', str(app_path)])
		print("\033[1;32m→ Launching Kjer GUI...\033[0m")
		return

	# Find electron (local node_modules preferred, falls back to global)
	electron_bin = _ensure_electron(desktop_dir)
	if electron_bin and desktop_dir.exists():
		subprocess.Popen([electron_bin, str(desktop_dir)])
		print("\033[1;32m→ Launching Kjer GUI...\033[0m")
		return

	print("\033[1;31m✗ Could not launch GUI.\033[0m")
	print(f"  Run: bash installer/kjer-install.sh  then try again.")
	os._exit(1)


